(521, 85, 0, '(3x+y) 2', '<p><span class="a" style="left: 759px; top: 3321px; word-spacing: 20px;">resolva: (3x+y)²</span></p>\n<div class="ff1" style="font-size: 116px;">  <span class="a" style="left: 1210px; top: 3321px;"><br /></span></div>', 1, '<div class="ff1" style="font-size: 116px;"><span class="a" style="left: 759px; top: 3439px; font-size: small;">(3x+y)</span><span class="a" style="left: 1042px; top: 3424px; font-size: small;">²</span><span class="a" style="left: 1101px; top: 3439px; word-spacing: -1px; font-size: small;">= (3x)²</span><span class="a" style="left: 1392px; top: 3439px; font-size: small;">+2.3x.y+y</span><span class="a" style="left: 1817px; top: 3424px; font-size: small;">²</span><span class="a" style="left: 1876px; top: 3439px; font-size: small;">=</span><span class="a" style="left: 1960px; top: 3438px; font-size: small;">9x</span><span class="a" style="left: 2065px; top: 3423px; font-size: small;">²</span><span class="a" style="left: 2098px; top: 3438px; font-size: small;">+6xy+y²</span></div>', 1, 1.0000000, 0.3333333, 'multichoice', 1, 'moodle.ufsc.br+131210211604+lNm8tj', 'moodle.ufsc.br+131210211604+MwEv12', 0, 1386710164, 1386710164, 2, 2),
(522, 85, 0, 'asso', '<p>Associe:</p>', 1, '<table border="1" cellspacing="0" cellpadding="0" align="center">\n<tbody>\n<tr>\n<td valign="top" width="335">\n<p align="center"><strong>Produtos notáveis</strong></p>\n</td>\n<td valign="top" width="263">\n<p align="center"><strong>Exemplos</strong></p>\n</td>\n</tr>\n<tr>\n<td valign="top" width="335">\n<p>(a+b)<sup>2</sup> = a<sup>2</sup>+2ab+b<sup>2</sup></p>\n</td>\n<td valign="top" width="263">\n<p>(x+3)<sup>2</sup> = x<sup>2</sup>+6x+9</p>\n</td>\n</tr>\n<tr>\n<td valign="top" width="335">\n<p>(a-b)<sup>2</sup> = a<sup>2</sup>-2ab+b<sup>2</sup></p>\n</td>\n<td valign="top" width="263">\n<p>(x-3)<sup>2</sup> = x<sup>2</sup>-6x+9</p>\n</td>\n</tr>\n<tr>\n<td valign="top" width="335">\n<p>(a+b)(a-b) = a<sup>2</sup>-b<sup>2</sup></p>\n</td>\n<td valign="top" width="263">\n<p>(x+3)(x-3) = x<sup>2</sup>-9</p>\n</td>\n</tr>\n<tr>\n<td valign="top" width="335">\n<p>(x+a)(x+b) = x<sup>2</sup>+(a+b)x+ab</p>\n</td>\n<td valign="top" width="263">\n<p>(x+2)(x+3) = x<sup>2</sup>+5x+6</p>\n</td>\n</tr>\n<tr>\n<td valign="top" width="335">\n<p>(a+b)<sup>3</sup> = a<sup>3</sup>+3a<sup>2</sup>b+3ab<sup>2</sup>+b<sup>3</sup></p>\n</td>\n<td valign="top" width="263">\n<p>(x+2)<sup>3</sup> = x<sup>3</sup>+6x<sup>2</sup>+12x+8</p>\n</td>\n</tr>\n<tr>\n<td valign="top" width="335">\n<p>(a-b)<sup>3</sup> = a<sup>3</sup>-3a<sup>2</sup>b+3ab<sup>2</sup>-b<sup>3</sup></p>\n</td>\n<td valign="top" width="263">\n<p>(x-2)<sup>3</sup> = x<sup>3</sup>-6x<sup>2</sup>+12x-8</p>\n</td>\n</tr>\n<tr>\n<td valign="top" width="335">\n<p>(a+b)(a<sup>2</sup>-ab+b<sup>2</sup>) = a<sup>3</sup>+b<sup>3</sup></p>\n</td>\n<td valign="top" width="263">\n<p>(x+2)(x<sup>2</sup>-2x+4) = x<sup>3</sup>+8</p>\n</td>\n</tr>\n<tr>\n<td valign="top" width="335">\n<p>(a-b)(a<sup>2</sup>+ab+b<sup>2</sup>) = a<sup>3</sup>-b<sup>3</sup></p>\n</td>\n<td valign="top" width="263">\n<p>(x-2)(x<sup>2</sup>+2x+4) = x<sup>3</sup>-8</p>\n</td>\n</tr>\n</tbody>\n</table>', 1, 1.000