INSERT INTO `mdl_question` (`id`, `category`, `parent`, `name`, `questiontext`, `questiontextformat`, `generalfeedback`, `generalfeedbackformat`, `defaultmark`, `penalty`, `qtype`, `length`, `stamp`, `version`, `hidden`, `timecreated`, `timemodified`, `createdby`, `modifiedby`) VALUES
(581, 91, 0, 'continuidade', '<p>uma função f dada por f(x) =<img src="http://www.pucrs.br/famat/silveira/calculoa/m1/m1_lista2_arquivos/image087.gif" alt="" width="60" height="44" />  para <span> </span><sub><img src="http://www.pucrs.br/famat/silveira/calculoa/m1/m1_lista2_arquivos/image089.gif" alt="" width="39" height="19" /></sub> e <span> </span><sub><img src="http://www.pucrs.br/famat/silveira/calculoa/m1/m1_lista2_arquivos/image091.gif" alt="" width="36" height="19" /></sub> , é uma função contínua em zero? caso seja, calcule f(0), caso não seja responda null.</p>', 1, '', 1, 1.0000000, 0.3333333, 'shortanswer', 1, 'moodle.ufsc.br+140509064453+wHxZEF', 'moodle.ufsc.br+140509064453+3cin1x', 0, 1399617893, 1399617893, 2, 2),
